<?phpclass AddTask{    public static function getTask()    {        $db = Db::getConnection();       if (isset($_POST['title']) && isset($_POST['text']) && isset($_POST['tdate'])) {            $title = strip_tags(trim($_POST['title']));            $text = strip_tags(trim($_POST['text']));            $stats = $_POST['stats'];            $tdate = $_POST['tdate'];            $db->query("INSERT INTO task (`title`, `text`, `status`, `tdate`)VALUES ('$title', '$text', '$stats' , '$tdate')");        }    }}