<?phpclass Login{    /**     * Returns an array of tasks items     */    public static function getUsers()    {        $db = Db::getConnection();        if (isset($_POST['login']) && isset ($_POST['password'])){        $login = $_POST['login'];        $password = md5($_POST['password']);     $res = array();     $sql =$db->query( "SELECT * FROM users WHERE `login`='$login' AND `password`='$password'");     $i = 0;     while($row = $sql->fetch()) {         $res[$i]['id'] = $row['id'];         $res[$i]['login'] = $row['login'];         $res[$i]['password'] = $row['password'];         $i++;     }     return $res; }    }}//$smt = $db->prepare($sql);//$smt->bindValue(':login', $login, PDO::PARAM_STR);//$smt->bindValue(':password', $password, PDO::PARAM_STR);//$smt->execute();//$res = $smt->fetch(PDO::FETCH_ASSOC);