option value="Father">Father</option>                                <option value="Child">Child</option>                            </select>                            <input type='hidden' name='tid2' value="<?= $taskItem ['id'] ?>">                            <button type='submit'>OK</button>                        </form>                        <?php endif;?>                        </p>                    </div>                    <div class="status">                        <b>Task status:</b>                        <?php if ( $taskItem ['status'] ==1){                            echo "<p style='color: #3cd627;'>done</p>";                        }else {                            echo "<p style='color: #d82213;'>not done</p>";                        }?>                        </p>                        <?php //check task status and print form with checkbox                           if($taskItem['user'] == $_SESSION['login'] && $taskItem['status'] == 0) : ?>                            <?php $stat = $taskItem['id'] ?>                            <form method="POST" action=''>                                <input type="checkbox" id="status" name="status" value="1">                                <label for="status">Check status</label>                                <input type="hidden" name="tid" value="<?= $taskItem['id'] ?>">                                <button type="submit">ok</button>                            </form>                        <?php endif; ?>                    </div>                </div>            </div>        <?php endforeach;?>    </div></body></html><?php else: echo "Please, <a href='/login'>Log in</a>  ";endif;